"use strict";var ProxAPI=function(settings){this.api_call=settings.translate;this.strategy=settings.strategy;this.retry_delay=(settings.retry_delay||60)*1e3};ProxAPI.prototype.call=function(params,callback,eventsCallback){var self=this;this.api_call(params,function(error,data,status){if(error){callback(error,null)}else{if(status.retry_delay){self.retry_delay=status.retry_delay*1e3}if(status.quota){if(self.strategy=="retry"){var message="Rate limit reached. Retrying in "+Math.ceil(self.retry_delay/1e3)+" seconds";if(eventsCallback)eventsCallback("retrying",message);setTimeout(function(){self.call(params,callback)},self.retry_delay)}else{callback("Rate limit exceeded",null)}}else{callback(error,data)}}})};ProxAPI.prototype.getLimitInfo=function(){return{retry_delay:this.retry_delay/1e3}};module.exports=ProxAPI;